import {useEffect, useState} from 'react';
import Card from '../UI/Card';
import MealItem from './MealItem/MealItem';
import classes from './AvailableMeals.module.css';

const AvailableMeals = () => {
	const [meals, setMeals] = useState([]);
	const [isLoading, setIsLoading] = useState(true)
	const [httpError, setHttpError] = useState(null)

	useEffect(() => {
		const fetchMeals = async () => {
			const response = await fetch('https://food-order-app-158af-default-rtdb.europe-west1.firebasedatabase.app/Meals.json');

			if (!response.ok) {
				throw new Error('Something went wrong');
			}

			const responseData = await response.json();

			//  trasformo da un ogetto ad un array
			const loadedMeals = [];

			//  setto una key in respondeData che userò per eseguire il destructuring
			for (const key in responseData) {
				loadedMeals.push({
					id: key,
					name: responseData[key].name,
					description: responseData[key].description,
					price: responseData[key].price,
				});
			}

			setMeals(loadedMeals);
			setIsLoading(false)
		};

		try {   // prova a chiamare fetchMeals()
			fetchMeals();
		} catch (error) {
			setIsLoading(false)
			setHttpError(error)
		}
	}, []);

	if (isLoading) {
		return (
			<section className={classes.MealsLoading}>
				<p>Loading...</p>
			</section>
		);
	}

	const mealsList = meals.map((meal) => (
		<MealItem
			key={meal.id}
			id={meal.id}
			name={meal.name}
			description={meal.description}
			price={meal.price}
		/>
	));

	return (
		<section className={classes.meals}>
			<Card>
				<ul>{mealsList}</ul>
			</Card>
		</section>
	);
};

export default AvailableMeals;
