import React, {useCallback, useState} from 'react';

const useHttp = () => {
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);

    const sendRequest = useCallback(async (requestCoinfig, appData) => {
        setIsLoading(true);
        setError(null);
        try {
            const response = await fetch(
                requestCoinfig.url, {
                    method: requestCoinfig.method ? requestCoinfig.method : 'GET',
                    headers: requestCoinfig.headers ? requestCoinfig.headers : {},
                    body: JSON.stringify(requestCoinfig.body) ? JSON.stringify(requestCoinfig.body) : 'null',
                }
            );

            if (!response.ok) {
                throw new Error('Request failed!');
            }

            const data = await response.json();
            applyData(data)

        } catch (err) {
            setError(err.message || 'Something went wrong!');
        }
        setIsLoading(false);
    }, [applyData]);

    return {
        isLoading,
        error,
        sendRequest,
    }
}
export default useHttp;
