import React, {useState} from 'react';

const useHttp = (requestCoinfig, applyData) => {
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);

    const sendRequest = async (taskText) => {
        setIsLoading(true);
        setError(null);
        try {
            const response = await fetch(
                requestCoinfig.url, {
                    method: requestCoinfig.method ? requestCoinfig.method : 'GET',
                    headers: requestCoinfig.headers,
                    body: JSON.stringify(requestCoinfig.body)
                }
            );

            if (!response.ok) {
                throw new Error('Request failed!');
            }

            const data = await response.json();
            applyData(data)

        } catch (err) {
            setError(err.message || 'Something went wrong!');
        }
        setIsLoading(false);
    };
    return {
        isLoading,
        error,
        sendRequest,
    }
}
export default useHttp;
