import {configureStore, createSlice} from "@reduxjs/toolkit";

const initialState = {counter: 0, showCounter: true}

const counterSlice = createSlice({  //  createSlice crea automaticamente action identifier per i differenti reducer
	name: 'counter',
	initialState,
	reducers: {
		increment(state) {
			state.counter++;
		},
		decrement(state) {
			state.counter--;
		},
		increase(state, action) {
			state.counter = state.counter + action.payload;
		},
		toggleCounter(state) {
			state.showCounter = !state.showCounter
		}
	}
})

//  reducer senza react toolkit
/*
 const counterReducer = (state = initialState, action) => {
 if (action.type === 'increment') {
 return {
 counter: state.counter + 1,
 showCounter: state.showCounter
 }
 }
 if (action.type === 'decrement') {
 return {
 counter: state.counter - 1,
 showCounter: state.showCounter
 }
 }
 if (action.type === 'increase') {
 return {
 counter: state.counter + action.amount, //  aggiungo un payload
 showCounter: state.showCounter
 }
 }
 if (action.type === 'toggle') {
 return {
 showCounter: !state.showCounter,
 counter: state.counter
 }
 }
 return state;
 } */

/* STORE
 configueStore è come createStore con la differenza che posso gestire multipli reducer
 */

const store = configureStore({
	reducer: counterSlice.reducer
})

//  Esporto le actions, non c'è bisogno che le definica, react toolkit fa tutto da solo
export const counterActions = counterSlice.actions;

export default store;
