import {Component} from 'react'
import {useDispatch, useSelector} from 'react-redux' //  hooks di redux
import classes from './Counter.module.css';

const Counter = () => {
	const dispatch = useDispatch()  //  Richiamo dispatch
	const counter = useSelector(state => state.counter) //  avrò sempre l'ultimo state aggiornato

	// funzione per incrementare
	const incrementHandler = () => {
		dispatch({type: 'increment'})
	}
	// funzione per decrementare
	const decrementHandler = () => {
		dispatch({type: 'decrement'})
	}

	const toggleCounterHandler = () => {
	};

	class Counter extends Component {
		incrementHandler() {
		}

		decrementHandler() {
			toggleCounterHandler()
		}

		render() {
			return (
				<main className={classes.counter}>
					<h1>Redux Counter</h1>
					<div className={classes.value}>{counter}</div>
					<div>
						<button onClick={incrementHandler}>Increment</button>
						<button onClick={decrementHandler}>Decrement</button>
					</div>
					<button onClick={toggleCounterHandler}>Toggle Counter</button>
				</main>
			);
		};
	}
}

export default Counter;
