import {Component} from 'react'
import {connect, useDispatch, useSelector} from 'react-redux' //  hooks di redux
import classes from './Counter.module.css';

const Counter = () => {
	const dispatch = useDispatch()  //  Richiamo dispatch
	const counter = useSelector(state => state.counter) //  avrò sempre l'ultimo state aggiornato

	// funzione per incrementare
	const incrementHandler = () => {
		dispatch({type: 'increment'})
	}
	// funzione per decrementare
	const decrementHandler = () => {
		dispatch({type: 'decrement'})
	}

	const toggleCounterHandler = () => {
	};

	// con connect
	class Counter extends Component {
		incrementHandler() {
			this.props.increment()
		}

		decrementHandler() {
			this.props.decrement()
		}

		toggleCounterHandler() {
		}

		render() {
			return (
				<main className={classes.counter}>
					<h1>Redux Counter</h1>
					<div className={classes.value}>{this.props.counter}</div>
					<div>
						<button onClick={this.incrementHandler.bind()}>Increment</button>
						<button onClick={this.decrementHandler.bind}>Decrement</button>
					</div>
					<button onClick={this.toggleCounterHandler}>Toggle Counter</button>
				</main>
			);
		};
	}
}

const mapStateToProps = state => {
	return {
		counter: state.counter
	}
}

const mapDispatchToProps = dispatch => {
	return {
		increment: () => dispatch({type: 'increment'}),
		decrement: () => dispatch({type: 'decrement'})
	}
}

export default connect(mapStateToProps, mapDispatchToProps)(Counter);
